        double x1 = (double) (paramXStart + radius);
        double y1 = (double) (paramYStart + radius);
        double x2 = (double) (paramXEnd - radius);
        double y2 = (double) (paramYEnd - radius);

        if (popPush) GlStateManager.pushMatrix();
