        if (Minecraft.getMinecraft().thePlayer.hurtTime > 3 && this.isToggled()) {
            combo = 0;
        }

        Minecraft.getMinecraft().fontRendererObj.drawString("[" + combo + " Combo]", this.x + 2, this.y + 2, -1);
    }
}

