   }

   public static boolean isOnLadder() {
      boolean onLadder = false;
      int y = (int)Minecraft.getMinecraft().thePlayer.getEntityBoundingBox().offset(0.0D, 1.0D, 0.0D).minY;

      for(int x = MathHelper.floor_double(Minecraft.getMinecraft().thePlayer.getEntityBoundingBox().minX); x < MathHelper.floor_double(Minecraft.getMinecraft().thePlayer.getEntityBoundingBox().maxX) + 1; ++x) {
         for(int z = MathHelper.floor_double(Minecraft.getMinecraft().thePlayer.getEntityBoundingBox().minZ); z < MathHelper.floor_double(Minecraft.getMinecraft().thePlayer.getEntityBoundingBox().maxZ) + 1; ++z) {
            Block block = Minecraft.getMinecraft().theWorld.getBlockState(new BlockPos(x, y, z)).getBlock();
            if (Objects.nonNull(block) && !(block instanceof BlockAir)) {
               if (!(block instanceof BlockLadder) && !(block instanceof BlockVine)) {
                  return false;
               }

               onLadder = true;
            }
         }
      }

      if (!onLadder && !Minecraft.getMinecraft().thePlayer.isOnLadder()) {
         return false;
      } else {
         return true;
      }
   }

   public static boolean isInsideBlock() {
      Minecraft.getMinecraft();
      int x = MathHelper.floor_double(Minecraft.getMinecraft().thePlayer.boundingBox.minX);

      while(true) {
         Minecraft.getMinecraft();
         if (x >= MathHelper.floor_double(Minecraft.getMinecraft().thePlayer.boundingBox.maxX) + 1) {
            return false;
         }

         Minecraft.getMinecraft();
         int y = MathHelper.floor_double(Minecraft.getMinecraft().thePlayer.boundingBox.minY);

         while(true) {
            Minecraft.getMinecraft();
            if (y >= MathHelper.floor_double(Minecraft.getMinecraft().thePlayer.boundingBox.maxY) + 1) {
               ++x;
               break;
            }

            Minecraft.getMinecraft();
            int z = MathHelper.floor_double(Minecraft.getMinecraft().thePlayer.boundingBox.minZ);

            while(true) {
               Minecraft.getMinecraft();
               if (z >= MathHelper.floor_double(Minecraft.getMinecraft().thePlayer.boundingBox.maxZ) + 1) {
                  ++y;
                  break;
               }

               Minecraft.getMinecraft();
               Block block = Minecraft.getMinecraft().theWorld.getBlockState(new BlockPos(x, y, z)).getBlock();
               if (block != null && !(block instanceof BlockAir)) {
                  Minecraft.getMinecraft();
                  WorldClient var10001 = Minecraft.getMinecraft().theWorld;
                  BlockPos var10002 = new BlockPos(x, y, z);
                  Minecraft.getMinecraft();
                  AxisAlignedBB boundingBox;
                  if ((boundingBox = block.getCollisionBoundingBox(var10001, var10002, Minecraft.getMinecraft().theWorld.getBlockState(new BlockPos(x, y, z)))) != null) {
                     Minecraft.getMinecraft();
                     if (Minecraft.getMinecraft().thePlayer.boundingBox.intersectsWith(boundingBox)) {
                        return true;
                     }
                  }
               }

               ++z;
            }
         }
      }
   }

   public static float[] getBlockRotations(double x, double y, double z) {
      double var4 = x - Minecraft.getMinecraft().thePlayer.posX + 0.5D;
      double var6 = z - Minecraft.getMinecraft().thePlayer.posZ + 0.5D;
      double var8 = y - (Minecraft.getMinecraft().thePlayer.posY + (double)Minecraft.getMinecraft().thePlayer.getEyeHeight() - 1.0D);
      double var14 = (double)MathHelper.sqrt_double(var4 * var4 + var6 * var6);
      float var12 = (float)(Math.atan2(var6, var4) * 180.0D / 3.141592653589793D) - 90.0F;
      return new float[]{var12, (float)(-Math.atan2(var8, var14) * 180.0D / 3.141592653589793D)};
   }

   public static float[] getFacingRotations(BlockPos pos, EnumFacing facing) {
      EntityXPOrb temp = new EntityXPOrb(Minecraft.getMinecraft().theWorld);
      temp.posX = (double)pos.getX() + 0.5D;
      temp.posY = (double)pos.getY() + 0.5D;
      temp.posZ = (double)pos.getZ() + 0.5D;
      temp.posX += (double)facing.getDirectionVec().getX() * 0.25D;
      temp.posZ += (double)facing.getDirectionVec().getZ() * 0.25D;
      temp.posY += 0.5D;
      return getRotationsNeeded(temp);
   }

   private static float[] getRotationsNeeded(Entity entity) {
      double posX = entity.posX - Minecraft.getMinecraft().thePlayer.posX;
      double posY = entity.posY - (Minecraft.getMinecraft().thePlayer.posY + (double)Minecraft.getMinecraft().thePlayer.getEyeHeight());
      double posZ = entity.posZ - Minecraft.getMinecraft().thePlayer.posZ;
      double var14 = (double)MathHelper.sqrt_double(posX * posX + posZ * posZ);
      float yaw = (float)(Math.atan2(posZ, posX) * 180.0D / 3.141592653589793D) - 90.0F;
      float pitch = (float)(-(Math.atan2(posY, var14) * 180.0D / 3.141592653589793D));
      return new float[]{yaw, pitch};
   }
}

